import React from 'react';
import { ChatLogin } from "./components/ChatLogin";
import { useStateValue } from "./Context-Reducer/StateProvider";
import { ChatApp } from "./components/ChatApp";

export const App = () => {
    const [{ user }] = useStateValue();

    return (
        <>
            {
                !user? (
                    <ChatLogin />
                    ) : (
                    <ChatApp />
                )
            }

            </>
    )
}
